package sample

tailrec fun calculateScore(input: String): Int {
    if (input == "") return 0

    var score = 0
    if (input[0] != '-') score += input[0].digitToInt()
    if (input[1] != '-') score += input[1].digitToInt()

    return score + calculateScore(input.drop(3).dropWhile { it == '|' })
}
